import React, { useMemo } from "react";
import styles from "../cssFiles/container.module.css";
import { useSelector, useDispatch } from "react-redux";
import { boxesActions } from "../store/boxCheck";

const RightContent = () => {
  let showResult = false;
  let winner = false;
  let loser = false;
  const dispatch = useDispatch();
  const currentEnabledIndex = useSelector(
    (state) => state.boxes.currentEnabledIndex
  );
  const secretCodeKey = useSelector(
    (state) => state.boxes.currentEnabledIndex
  );
  const circleStates = useSelector((state) => state.boxes.circleStates);
  const startButtonHandler = () => {
    dispatch(boxesActions.start());
    showResult = false;
    winner = false;
    loser = false;
  };
  
  useMemo(() => {
    if(currentEnabledIndex > 0){
    if (parseInt(circleStates[currentEnabledIndex].correctRightPlace + "") === 4) {
      showResult = true;
      winner = true;
      fetch(boxesActions.end);
    
    }
    else if (currentEnabledIndex === 7) {
      showResult = true;
      loser = true;
      fetch(boxesActions.end);
    }
    }
  }
  ,[currentEnabledIndex]);

  return (
    <div className={styles["container-right-content"]}>
      <div>Secret code</div>
      <div>{ }</div>
      <button
        type="button"
        onClick={startButtonHandler}
        className={styles["container-right-button"]}
      >
        start
      </button>
    </div>
  );
};
export default RightContent;
