import { createSlice } from "@reduxjs/toolkit";
//Math.floor(Math.random() * 10);

const boxesSlice = createSlice({
  name: "boxes",
  initialState: {
    boxesValues: [],
    RandomValues: Array(4)
      .fill()
      .map(() => Math.floor(10 * Math.random())), //fill array of size 4 with random numbers from 0 to 9
      circleStates: [],
    currentEnabledIndex: 0,
  },
  reducers: {
    setBoxes(state, action) {
      const value = action.payload;
      state.boxesValues.push(
        value
      );
      console.log(value);
      console.log(`the random key is ${state.RandomValues}`);
    },
    incrementEnableIndex(state) {
      state.currentEnabledIndex++;
    },
      check(state, action) {
         // state.RandomValues[]
          
      const checkValues = action.payload;
      checkValues.correctRightPlace++;
      checkValues.correctWrongPlace++;
      checkValues.uncorrect++;
      state.circleStates.push({
        index: checkValues.index,
        correctRightPlace: checkValues.correctRightPlace,
        correctWrongPlace: checkValues.correctWrongPlace,
        uncorrect: checkValues.uncorrect,
      });
    },
  },
});

export const boxesActions = boxesSlice.actions;

export default boxesSlice;
