import React, { useEffect } from 'react';
import { useSelector, useDispatch } from "react-redux";
import styles from "../cssFiles/circles.module.css";


const Circle = (props) => {
    let correctRightPlace = [];
    let correctWrongPlace = [];
    
    const circleStates = useSelector((state) => state.boxes.circleStates);
    const blueCirclers = () => {
        return <div className={`${styles["circle"]} ${styles["blue-circle"]}`} />
     }
    const whiteCirclers = () => { 
        return <div className={`${styles["circle"]} ${styles["white-circle"]}`} />
    }
    useEffect(() => {
   correctRightPlace = Array.apply(
    null,
    Array(parseInt(circleStates[props.key].correctRightPlace + ""))
  ).map(blueCirclers);
   correctWrongPlace = Array.apply(
    null,
    Array(parseInt(circleStates[props.key].correctWrongPlace + ""))
        ).map(whiteCirclers);
    });

    return (<>
        {correctRightPlace}
        {correctWrongPlace}
    </>);
};

export default Circle;
