import React, {useState} from "react";
import { useSelector, useDispatch } from "react-redux";
import { boxesActions } from "../store/boxCheck";
import styles from "../cssFiles/boxes.module.css";

const Box = (props) => {
  const currentEnabledIndex = useSelector(
    (state) => state.boxes.currentEnabledIndex
  );
  const dispatch = useDispatch();
  let [wrongInput, setWrongInput] = useState(false);
  let rowBoxes = Array.apply(null, Array(4)).map((boxValue, index) => {
    return (
      <div key={props.boxLineIndex * 10 + index}>
        <input
          type="text"
          maxLength="1"
          size="10"
          className={
            (props.boxLineIndex !==  currentEnabledIndex) ? styles.disabled : (wrongInput) ? styles["wrongInput"] : ""
          }
          autoComplete="off"
          onChange={(e) => handleChange(index, e)}
        />
      </div>
    );
  });

  const handleChange = (index, event) => {
    let value = event.target.value;
    if ( /^\d+$/.test(value)) {
      dispatch(boxesActions.setBoxes({ value, index }));
    } else {
      setWrongInput(true);
    }
  };
  return <>{rowBoxes}</>;
};
export default Box;
