import { createSlice } from '@reduxjs/toolkit';
//Math.floor(Math.random() * 10); //random number from 0 to 9

const boxesSlice = createSlice({
    name: 'boxes',
    initialState: {
        boxesValues: [],
        RandomValues: Array.apply(null, Array(4)).map(Number.prototype.valueOf,Math.floor(Math.random() * 10)),
        correctRightPlace: 0,
        correctWrongPlace: 0,
        uncorrect: 0,
        currentEnabledIndex: 0,
    },
    reducers: {
        setBoxes(state, action) {
            const newBox = action.payload;
            state.boxesValues.push({
                index: newBox.index,
                value: newBox.value
            });
            console.log(newBox.index);
            console.log(newBox.value);
            console.log(`the random key is ${RandomValues}`);
        },
        incrementEnableIndex(state) {
            state.currentEnabledIndex++;
          },
    },
});

export const boxesActions = boxesSlice.actions;

export default boxesSlice;
